mapper.select:
SELECT
    COUNT(*)
FROM open_chat_room_membership_requests
WHERE requester_id = #{requesterId}
AND status = #{status}
AND deleted_at IS NULL

SELECT
    EXISTS(
        SELECT
            *
        FROM open_chat_room_membership_requests
        WHERE requester_id = #{requesterId}
        AND open_chat_room_id = #{openChatRoomId}
        AND status = #{status}
        AND deleted_at IS NULL
    )
